graphql.query:
query showClientProfile($uuid: ID!) {
  showClientProfile(uuid: $uuid) {
      uuid
      lastName
      firstName
      middleName
      email
      phone
      dob
      gender
      height
      weight
      livesWith
      employment
      educationCompleted
      medications {
        uuid
        name
        dosage
        physicianName
        physicianEmail
        timeOnMedication
        comments
      }
      vehicleInformations {
        uuid
        year
        make
        model
        color
        license
      }
      children {
        firstName
      }
      personalHistories {
        uuid
        details
      }
      emergencyContacts {
        uuid
        firstName
        middleName
        lastName
        phoneContacts {
            id
        }
        contactType
        email
      }
      collateralContacts {
        uuid
        firstName
        middleName
        lastName
        phoneContacts {
            id
        }
        contactType
        email
      }
   }
}
