graphql.query:
query showSexualVariable($uuid: ID!) {
  showSexualVariable(uuid: $uuid) {
    uuid
    orgasms_per_typical_average_week
    highest_no_of_orgasms_in_n_last_months
    age_at_first_exposure_to_sem
    no_of_times_viewed_sem_over_lifetime
    createdBy
    updatedBy
    createdAt
    updatedAt
  }
}

mutation createSexualVariable($input: CreateSexualVariableInput!) { 
  createSexualVariable(input: $input) {
     id
     uuid
     enrollmentId
     orgasms_per_typical_average_week
     highest_no_of_orgasms_in_n_last_months
     age_at_first_exposure_to_sem
     no_of_times_viewed_sem_over_lifetime
     createdAt
     updatedAt
   }
}
